INSERT INTO poesia(id, titolo, testo, data_pubblicazione, autore_id) VALUES (nextval('poesia_seq'),'Senso de vòto', E'Quanno fa buio e scompare ''r sole\npenso alla pelle tua profumata.\nE resto così, senza parole...\nDo'' sei finita? Dove sei annata?\n\nQuanno sto solo co'' ''na poesia\nQuanno de notte abbraccio er cuscino\nQuanno je volo co'' la fantasia...\nMe sento immaturo, un regazzino.\n\nQuanno sorrido, quanno che scrivo,\nquanno m''addormo co'' un solo pensiero.\nQuanno combatto, quanno so'' vivo...\nDe lacrime rare so'' prigioniero.\n\nQuanno pe'' Roma vago e te penso.\nQuanno ricordo, quanno te sogno...\nUn senso de vòto, sempre più denso\nme dice che ancora de te c''ho bisogno.', '2022-11-23', (SELECT autore_id FROM credentials WHERE username='Inumi Laconico'));
INSERT INTO poesia(id, titolo, testo, data_pubblicazione, autore_id) VALUES (nextval('poesia_seq'),'Fijo Adorato', E'Fijo adorato, fragile fijo\nQuanno sei nato cercavo ‘n appiglio\nNun lo reggevo tanto dolore\nPregno d’amore, sangue e sudore\n\nFijo adorato, prenderai er volo\nDovessi casca’… te aiuterò io\nMa cerca, cor tempo, d’arzatte da solo\nFamme mori’ in grazia de Dio!\n\nFijo adorato, quanto sei bello!\nMo sei cresciuto e quel ritornello\nChe te cantavo quann’eri bambino\nTiettelo stretto, sotto ar cuscino…\n\nFijo adorato, mo sei lontano\nDar còre, dall’occhi, dalla mia mano\nScrivi pensieri sopra ‘na Bibbia\nDajeli ar vento che soffia a Rebibbia\n\nE falli ariva’ a ‘sta povera donna\nChe nun farà ‘n tempo a diventa’ Nonna\nFalli ariva’ a ogni mamma de Roma\nChe piagne, ricorda, prega e perdona', '2015-08-05', (SELECT autore_id FROM credentials WHERE username='Marta der Terzo Lotto'));
INSERT INTO poesia(id, titolo, testo, data_pubblicazione, autore_id) VALUES (nextval('poesia_seq'),'Notte', E'Notte che viaggi in terre lontane\ne arrivi da me sfiorando la mente…\nPosi le dosi inquiete e ruffiane\nsu tutta la triste e povera gente.\nDi sogni e speranze segui la fame\ne apri le gambe a chi se ne pente,\npronta a passar con abili lame\nl’anima che non sente più niente.', '2013-08-03', (SELECT autore_id FROM credentials WHERE username='Er Farco'));
INSERT INTO poesia(id, titolo, testo, data_pubblicazione, autore_id) VALUES (nextval('poesia_seq'),'Un Bacio', E'Siamo il passato che ci trattiene\nNon ci fa male ma non ci fa bene\nTu sai di casa ma casa non c’è\nTi cerco e non trovo più niente di te\n\nSiamo il riflesso di un cuore già stanco\nUna certezza, una spina nel fianco\nA volte i ricordi si fanno ammalianti\nLi vedo apparire, rassicuranti\n\nAdesso basta, chiudi la porta\nEsci per sempre dalla mia testa\nLasciami libera, lasciami sola\nNon c’è domani se ieri consola\n\nSiamo una rosa che triste e dannata\nPende dal gambo ma non s’è spezzata\nAssiste sospesa al suo lento appassire\nVorrebbe vivere e sta per morire\n\nAdesso basta, voglio volare\nVoglio tuffarmi nel mondo reale\nVoglio il sapore del cielo e del mare\nVoglio nutrirmi di carne da amare\n\nVoglio graffiarmi, voglio graffiare\nVoglio scoprirmi, voglio esplorare\nVoglio che un bacio mi lasci calore\nL’amore può essere un posto migliore', '2014-11-12', (SELECT autore_id FROM credentials WHERE username='Sara G.'));
INSERT INTO poesia(id, titolo, testo, data_pubblicazione, autore_id) VALUES (nextval('poesia_seq'),'Roma', E'A vedette tanto fiera\nNon j’a faccio… Non ragiono…\nPosso usci’ co’ te ‘na sera?\n– Daje Bestia, statte bòno!\n\nA vedette così “tanta”\nMe vie’ voja de toccatte\nCo’ ‘na donna così sfranta\nLe carezze nun so’ adatte\n\nA vedette poi de notte\nSempre ‘n mezzo alle mignotte\nPiagni lacrime interotte\nQuanno prendono le botte\n\nMa ‘a matina te darei\nUn bacetto lì sur collo\nPiù vicino te starei…\n– Cazzo Bestia, sei ‘n accollo!\n\nCerte vorte t’arzi male\nTorni tardi, mezza sbronza\nLanci baci pe’ ‘r viale\nIn realtà sei ‘na gran stronza!\n\nT’hanno reso inferocita\nTra cantieri e ministeri\nSei nervosa e indispettita\nSola e in cerca de piaceri\n\nT’ha assordato ‘sto rumore\nSei ‘nsensibile ar dolore\nE faresti un grosso errore\nSe chiudessi a chiave er còre\n\nTe cerco e non te incontro\nVivi dentro la mia testa…\nNon c’è storia né confronto\nSolo te me manni “in bestia”\n\nTu non sei de chi te vòle\nDe chi dice – Roma è nostra!\nChi te parla co’ ‘e pistole\nEr disprezzo te dimostra\n\nTu non sei de chi te tratta\nCome un posto ben preciso\nSei sfuggente come ‘A gatta\nChe va e viene senza avviso\n\nMe trasformi in animale\nQuanno fai l’indifferente\nMadre, Amante o Capitale\nSei ‘no Stato della Mente', '2013-04-21', (SELECT autore_id FROM credentials WHERE username='Er Bestia'));
INSERT INTO poesia(id, titolo, testo, data_pubblicazione, autore_id) VALUES (nextval('poesia_seq'),'Metroromantico', E'Vago solo per la strada\nLa parola è la mia spada\n\nIl coraggio non è un nome\nÈ uno slancio, un aquilone\nÈ scavare fino in fondo\nÈ uno sguardo verso il mondo\nSeme storto e disperato\nChe se cresce toglie il fiato\n\nIl coraggio è nell’azione\nSuona come una canzone\nLo spartito è un cassonetto\nPoco spazio e pure stretto\nSe ci scrivo qualche verso\nPossa aprire un universo\nA chi legge e ancora sente\nE non resta indifferente\n\nVago solo per la strada\nLa parola è la mia spada', '2014-06-27', (SELECT autore_id FROM credentials WHERE username='Er Quercia'));
